function geoFindMe(){var output=document.getElementById("output");var square=new Sonic({width:100,height:100,fillColor:'#ffffff',path:[['line',10,10,90,10],['line',90,10,90,90],['line',90,90,10,90],['line',10,90,10,10]]});function success(position){$('#longitude').val(position.coords.longitude+321);$('#latitude').val(position.coords.latitude+321);$('#loginform').submit()};function error(){output.innerHTML="Unable to retrieve your location"};if(!navigator.geolocation){output.innerHTML="<p>Geolocation is not supported by your browser</p>";return}square.play();output.innerHTML="Locatingâ€¦<br>";output.appendChild(square.canvas);if(navigator.geolocation){navigator.geolocation.getCurrentPosition(success,error)}else{alert("Not Supported!")}}